import React from 'react';

class App extends React.Component(){
    constructor(){
        super();

        this.state = {
           pricedata:  null
        };
    }


    componentDidMount(){
        fetch('https://api.coindesk.com/v1/bpi/historical/close.json?start=2020-07-01&end=2020-07-07')
        .then(response => response.json())
        .then(data => 

        this.setState({ 
        btcUsdChart: data.bpi 

        }));
    };




    render(){
        let tempentries = Object.entries(this.state.pricedata);
        let chartdata = [[],[]];


        for (const element of tempentries) {
            chartdata.push(element[1]);
            chartdata.push(element[0]);
        };
        return(
            <div>
                <p>{chartdata[0]}</p>
                <p>{chartdata[1]}</p>
            </div>
        )
    };

}

export default App;
