import NewPosition from './Components/NewPositions.jsx'

//-------------------------------------------------
class App extends React.Component {
  constructor() {
    super();
    this.state = { 
      isLoading: false,
      Positions : []
     }
  }
componentDidMount(){
  this.setState({isLoading: true});

